package com.braininventory.leadsphere.user_service.entity;import com.braininventory.leadsphere.user_service.enums.Role;import jakarta.persistence.*;import lombok.Data;import java.time.LocalDateTime;import java.util.Date;@Entity@Table(name = "user")@Datapublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String firstName;    private String lastName;    private String email;    private String password;    private String mobileNo;    private LocalDateTime createdAt;    @Enumerated(EnumType.STRING)    private Role role;    private Boolean isActive;}