package com.braininventory.leadsphere.user_service.entity;import com.braininventory.leadsphere.user_service.enums.Role;import jakarta.persistence.*;import lombok.Data;@Entity@Table(name = "user")@Datapublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String firstName;    private String lastName;    private String email;    private String password;    private String mobileNo;    @Enumerated(EnumType.STRING)    private Role role;    private String planName;}