package com.braininventory.leadsphere.user_service.service.impl;import com.braininventory.leadsphere.user_service.service.MailService;import jakarta.mail.internet.MimeMessage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.mail.javamail.JavaMailSender;import org.springframework.mail.javamail.MimeMessageHelper;import org.springframework.stereotype.Service;import org.thymeleaf.TemplateEngine;import org.thymeleaf.context.Context;@Servicepublic class MailServiceImpl implements MailService {    private final JavaMailSender mailSender;    private final TemplateEngine templateEngine;    @Autowired    public MailServiceImpl(JavaMailSender mailSender, TemplateEngine templateEngine) {        this.mailSender = mailSender;        this.templateEngine = templateEngine;    }    @Override    public void sendInviteEmail(String toEmail, String token) {        try {            String inviteLink = "http://localhost:8081/invites/accept?token=" + token;            Context context = new Context();            context.setVariable("inviteLink", inviteLink);            String htmlContent = templateEngine.process("invite-email", context);            MimeMessage mimeMessage = mailSender.createMimeMessage();            MimeMessageHelper helper = new MimeMessageHelper(mimeMessage, true, "UTF-8");            helper.setTo(toEmail);            helper.setSubject("You're invited to LeadSphere!");            helper.setText(htmlContent, true);            mailSender.send(mimeMessage);        } catch (Exception e) {            throw new RuntimeException("Failed to send invite email", e);        }    }}